pragma solidity ^0.4.17;

import './Owned.sol';
import './DougEnabled.sol';
import './ContractProvider.sol';
import './Permissions.sol';
import './LetterOfCredit.sol';

contract LocManager is Owned, DougEnabled {

  function LocManager() public {
    owner = msg.sender;
  }

  function isDocumentRegistered(string sdocHash) public constant returns (bool, bytes32, bytes32, bytes32, bytes32) {
    address loc = ContractProvider(DOUG).contracts("loc");
    require(loc != 0x0);
    //address perms = ContractProvider(DOUG).contracts("perms"); //gaan we werken met permissions?
    //require(loc != 0x0 && perms != 0x0 && Permissions(perms).getPermission(msg.sender) == 1);
    return LetterOfCredit(loc).isDocumentRegistered(sdocHash);
  }

  function registerDocument(string sdocHash, bytes32 title, bytes32 releaseDate, bytes32 authorName, bytes32 authorMail) public returns (bool, bytes32, bytes32, bytes32, bytes32) {
    address loc = ContractProvider(DOUG).contracts("loc");
    require(loc != 0x0);
    return LetterOfCredit(loc).registerDocument(sdocHash, title, releaseDate, authorName, authorMail);
  }

  function getDocumentListFromAuthor(bytes32 authorName) external constant returns (uint256 nrOfDocuments, bytes32 authorMail, bytes32[50] titles, bytes32[50] releaseDates) {
    address loc = ContractProvider(DOUG).contracts("loc");
    require(loc != 0x0);
    return LetterOfCredit(loc).getDocumentListFromAuthor(authorName);
  }

  //----------------------------

  function setPermission(address addr, uint8 permLvl) public returns (bool res) {
    if (msg.sender != owner) {
      return false;
    }
    address perms = ContractProvider(DOUG).contracts("perms");
    if (perms == 0x0) {
      return false;
    }
    return Permissions(perms).setPermission(addr, permLvl);
  }
}
