pragma solidity ^0.4.17;

import './LocManagerEnabled.sol';
import './ContractProvider.sol';
import './PermissionsDb.sol';

contract Permissions is LocManagerEnabled {

  function setPermission(address addr, uint8 perm) public returns (bool res) {
    if (!isLocManager()) {
      return false;
    }
    address permdb = ContractProvider(DOUG).contracts("permsdb");
    if (permdb == 0x0) {
      return false;
    }
    return PermissionsDb(permdb).setPermission(addr, perm);
  }

  function getPermission(address addr) public view returns (uint8 perm) {
    require(isLocManager());
    address permdb = ContractProvider(DOUG).contracts("permsdb");
    require(permdb != 0x0);
    return PermissionsDb(permdb).perms(addr);
  }

}
